mapper.delete:
delete from shopping_mall_cart
where id = #{cartId}
and buyer_id = #{buyerId}

mapper.select:
select

from shopping_mall_cart
where product_id = #{productId}
and buyer_id = #{buyerId}

mapper.update:
update shopping_mall_cart
set check_status = #{checkStatus},
update_time = now()
where id = #{cartId}
and buyer_id = #{buyerId}
